import React from "react";

const BoardRow = (props) => {
    const { cellValues, id, handleChange } = props

    const columns = [1, 2, 3, 4, 5, 6, 7, 8, 9]
    const getCellValue = (id, columnNum) => {
      let num = ((parseInt(id) - 1) * 9) + (columnNum - 1) 

      return cellValues[num]
    }

    return (
      <tr>
      {columns.map((element) => {
        const cellId = id + element
        const cellValue = getCellValue(id, element)

        return (
          <td key={element}>
            <input type="text"
            name="name"
            value={cellId}
            onChange={handleChange}
            id={cellId}
            />
          </td>
        )
      })}
      </tr>
    )
}


export default BoardRow
