import React from "react";
import Banner from "./Banner";
import Tools from "./Tools";
import Board from "./Board";
import Footer from "./Footer";
import InfoBox from "./InfoBox";

import createFullGrid from "../utils/createFullGrid/createFullGrid";
import copyGrid from "../utils/copyGrid/copyGrid";
import removeNumbers from "../utils/removeNumbers/removeNumbers";

import "../styles/App.css";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      activeCells: [],
      difficulty: 5,
      feedback: true,
    };
    this.handleChange = this.handleChange.bind(this);
    this.newGame = this.newGame.bind(this);
  }
  solve = () => {
    console.log("solve");
  };
  stop = () => {
    console.log("stop");
  };
  newGame = () => {
    this.createNewGame(this.state.difficulty);
  };
  deleteGame = () => {
    console.log("delete game");
  };
  goBack = () => {
    console.log("go back");
  };
  setActiveCells = (row, col) => {
    const activeCells = [];
    activeCells.push(row, col);
    this.setState((state) => {
      return {
        activeCells,
      };
    });
  };

  setDifficulty = (e) => {
    const value = parseInt(e.target.value);

    this.setState(() => {
      return {
        difficulty: value,
      };
    });
  };
  changeFeedbackToggle = (e) => {
    const value = e.target.value === 'yes' ? true : false

    this.setState((state) => {
      if (state.feedback !== value )
      return {
        feedback: value
      }
    })
  }

  handleChange = (e, rowIndex, colIndex) => {
    const solvedGrid = this.state.solvedCellValues;
    if (
      this.state.cellValues.length === 0 ||
      this.state.solvedCellValues.length === 0
    ) {
      alert("Please start the game");
      return this.state;
    }
    const value = parseInt(e.target.value);
    if (isNaN(value)) {
      alert("You must enter a valid whole number between 0 and 9");
      return this.state;
    }
    if (solvedGrid[rowIndex][colIndex] !== value) {
      alert("incorrect");
      return this.state;
    }
    this.setState((prevState) => {
      const currentGrid = prevState.cellValues;
      currentGrid[rowIndex][colIndex] = value;
      return {
        cellValues: currentGrid,
      };
    });

    return this.state;
  };
  componentDidUpdate() {}
  createNewGame = (difficulty) => {
    const solvedGrid = createFullGrid();
    const solvedGridCopy = copyGrid(solvedGrid);
    const challengeGrid = removeNumbers(solvedGridCopy, difficulty);

    this.setState((state) => {
      return {
        cellValues: challengeGrid,
        solvedCellValues: solvedGrid,
      };
    });
  };

  componentDidMount() {}

  render() {
    return (
      <div className='app'>
        <Banner />
        <section className='app-body'>
          <Tools
          />
          <Board  
          />
          <InfoBox
          />
        </section>
        <Footer />
      </div>
    );
  }
}

export default App;
