// Create new game

// set active cells
export const setActiveCells = (row, col) => {
  const activeCells = []
  activeCells.push(row, col)
  return {
    type: 'SET_ACTIVE_CELLS',
    activeCells
  }
}

// set cell value
export const setCellValue = (row, col) => {
  return {
    type: 'SET_CELL_VALUE',
    row,
    col
  }
}


// Tools

export const createNewGame = () => {
  return {
    type: 'CREATE_NEW_GAME'
  }
}
