// Create new game

// set active cells
export const setActiveCells = (row, col) => {
  const activeCells = []
  activeCells.push(row, col)
  return {
    type: 'SET_ACTIVE_CELLS',
    activeCells
  }
}

// set cell value
export const setCellValue = (row, col, value) => {
  return {
    type: 'SET_CELL_VALUE',
    row,
    col,
    value
  }
}
// Tools

export const createNewGame = () => {
  return {
    type: 'CREATE_NEW_GAME'
  }
}

// set difficulty
export const setDifficulty = (value) => ({
  type: 'SET_DIFFICULTY',
  value
})
