// Create new game

setActiveCells = (row, col) => {
  const activeCells = [];
  activeCells.push(row, col);
  this.setState((state) => {
    return {
      activeCells,
    };
  });
};

export const setActiveCells = (row, col) => {
  const activeCells = []
  activeCells.push(row, col)
  return {
    type: 'SET_ACTIVE_CELLS',
    activeCells
  }
}

// Tools

export const createNewGame = () => {
  return {
    type: 'CREATE_NEW_GAME'
  }
}
