// Create new game

// set active cells
export const setActiveCells = (row, col) => {
  return {
    type: 'SET_ACTIVE_CELLS',
    row,
    col
  }
}

// set cell value
export const setCellValue = (row, col, value) => {
  return {
    type: 'SET_CELL_VALUE',
    row,
    col,
    value
  }
}
// Tools
// Create new game
export const createNewGame = () => {
  return {
    type: 'CREATE_NEW_GAME'
  }
}
// setFeedback true/false
export const setFeedback = (value) => {
  return {
    type: 'SET_FEEDBACK',
    value
  }
}

// set difficulty
export const setDifficulty = (value) => ({
  type: 'SET_DIFFICULTY',
  value
})
