import React from "react";
import "../styles/BoardRow.css";

const BoardRow = props => {
  const { cellValues, rowIndex, handleChange, setActiveCells } = props;
  const columns = [1, 2, 3, 4, 5, 6, 7, 8, 9];
  const getValue = (row, column) => {
    return cellValues[row][column];
  };

  return (
    <tr>
      {columns.map(element => {
        const colIndex = element - 1;
        const cellValue = getValue(rowIndex, colIndex);

        return (
          <td key={element} className="square-cell">
            <input
              type="text"
              defaultValue={cellValue}
              onClick={e => setActiveCells(rowIndex, colIndex)}
              onChange={e => handleChange(e, rowIndex, colIndex)}
              className={`square-input `}
              maxLength='1'
            />
          </td>
        );
      })}
    </tr>
  );
};

export default BoardRow;
